from flask import Flask

app = Flask(__name__)


@app.route('/')
def hello_world():
    return "<h1 style='text-align:center; color:red'>Hello, Universe!</h1>" \
           "<p>This is a paragraph</p>" \
           "<img src='data:image/jpeg;base64,' width=500 alt='house_picture'>"

# make the bye function return the bolded bye string
def make_bold(function):
    def wrapper():
        return "<b>" + function() + "</b>"
    return wrapper

@app.route("/bye")
@make_bold
def bye():
    return "Bye!"


@app.route("/username/<name>")
def say_name(name):
    return f"hi I am {name}"


result = bye()
print(result)

if __name__ == "__main__":
    app.run(debug=True, port=8080)
